      "template": "\"use client\";\n\nimport { useState } from \"react\";\nimport Link from \"next/link\";\nimport ThemeToggle from \"./common/ThemeToggle\";\nimport RiseLogo from \"@/components/common/RiseLogo\";\nimport AuthModal from \"./AuthModal\";\nimport { useAuth } from \"@/contexts/AuthContext\";\nimport { LogOut, User } from \"lucide-react\";\n\nexport default function Navbar() {\n  const [authModalOpen, setAuthModalOpen] = useState(false);\n  const [authModalTab, setAuthModalTab] = useState<'login' | 'signup'>('login');\n  const { user, isAuthenticated, logout } = useAuth();\n  const [showUserMenu, setShowUserMenu] = useState(false);\n\n  const handleLogout = async () => {\n    await logout();\n    setShowUserMenu(false);\n  };\n\n  return (\n    <>\n      <nav className=\"fixed top-0 left-0 right-0 z-50 bg-white/80 dark:bg-[#2D1B1B]/80 backdrop-blur-xl border-b border-[#FFD4D4] dark:border-[#3D2626]\">\n        <div className=\"max-w-7xl mx-auto px-6 py-4\">\n          <div className=\"flex items-center justify-between\">\n            {/* Logo - LEFT SIDE */}\n            <Link\n              href=\"/\"\n              className=\"flex items-center gap-3 hover:opacity-80 transition-opacity\"\n            >\n              <RiseLogo className=\"w-10 h-10\" />\n              <span className=\"text-2xl font-bold text-[#2D1B1B] dark:text-[#FFF5F5]\">\n                Rise\n              </span>\n            </Link>\n\n            {/* Right side - Theme Toggle + Auth */}\n            <div className=\"flex items-center gap-4\">\n              <ThemeToggle />\n\n              {isAuthenticated && user ? (\n                <div className=\"relative\">\n                  <button\n                    onClick={() => setShowUserMenu(!showUserMenu)}\n                    className=\"flex items-center gap-2 px-4 py-2 rounded-xl hover:bg-[#FFE5E5] dark:hover:bg-[#3D2626] transition-all\"\n                  >\n                    <div className=\"w-8 h-8 bg-gradient-to-br from-[#E87C7C] to-[#E05D5D] rounded-full flex items-center justify-center text-white font-semibold text-sm\">\n                      {user.firstName[0]}{user.lastName[0] || ''}\n                    </div>\n                    <span className=\"text-[#2D1B1B] dark:text-[#FFF5F5] font-medium hidden sm:block\">\n                      {user.firstName}\n                    </span>\n                  </button>\n\n                  {showUserMenu && (\n                    <div className=\"absolute right-0 top-full mt-2 w-48 bg-white dark:bg-[#2D1B1B] border border-[#FFD4D4] dark:border-[#3D2626] rounded-xl shadow-xl overflow-hidden\">\n                      <Link\n                        href=\"/dashboard\"\n                        className=\"flex items-center gap-2 px-4 py-3 hover:bg-[#FFE5E5] dark:hover:bg-[#3D2626] transition-colors text-[#2D1B1B] dark:text-[#FFF5F5]\"\n                        onClick={() => setShowUserMenu(false)}\n                      >\n                        <User className=\"w-4 h-4\" />\n                        Dashboard\n                      </Link>\n                      <button\n                        onClick={handleLogout}\n                        className=\"w-full flex items-center gap-2 px-4 py-3 hover:bg-[#FFE5E5] dark:hover:bg-[#3D2626] transition-colors text-[#E85D75]\"\n                      >\n                        <LogOut className=\"w-4 h-4\" />\n                        Sign Out\n                      </button>\n                    </div>\n                  )}\n                </div>\n              ) : (\n                <>\n                  <button\n                    onClick={() => {\n                      setAuthModalTab('login');\n                      setAuthModalOpen(true);\n                    }}\n                    className=\"text-[#2D1B1B] dark:text-[#FFF5F5] hover:text-[#E87C7C] font-medium transition-colors hidden sm:block\"\n                  >\n                    Login\n                  </button>\n\n                  <button\n                    onClick={() => {\n                      setAuthModalTab('signup');\n                      setAuthModalOpen(true);\n                    }}\n                    className=\"bg-[#E87C7C] hover:bg-[#E05D5D] text-white px-6 py-2 rounded-xl font-semibold transition-all hover:scale-105\"\n                  >\n                    Sign Up\n                  </button>\n                </>\n              )}\n            </div>\n          </div>\n        </div>\n      </nav>\n\n      {/* Auth Modal */}\n      <AuthModal \n        isOpen={authModalOpen} \n        onClose={() => setAuthModalOpen(false)}\n        initialError={undefined}\n      />\n    </>\n  );\n}\n"
