      "template": "interface RiseLogoProps {\n  className?: string;\n}\n\nexport default function RiseLogo({ className = \"w-10 h-10\" }: RiseLogoProps) {\n  return (\n    <svg xmlns=\"http://www.w3.org/2000/svg\" viewBox=\"0 0 100 100\" className={className}>\n      {/* Outer tilted square */}\n      <rect\n        x=\"20\"\n        y=\"20\"\n        width=\"60\"\n        height=\"60\"\n        rx=\"12\"\n        transform=\"rotate(15 50 50)\"\n        fill=\"#E87C7C\"\n      />\n      {/* Inner offset square */}\n      <rect\n        x=\"35\"\n        y=\"35\"\n        width=\"30\"\n        height=\"30\"\n        rx=\"6\"\n        transform=\"rotate(-10 50 50)\"\n        fill=\"#FCC0CE\"\n      />\n      {/* Dot or signal element */}\n      <circle cx=\"72\" cy=\"28\" r=\"6\" fill=\"#FBCFE8\" />\n    </svg>\n  );\n}\n"
