      "template": "import { ReactNode } from \"react\";\n\ninterface StatCardProps {\n  icon: ReactNode;\n  label: string;\n  value: string;\n  color: string;\n}\n\nexport default function StatCard({ icon, label, value, color }: StatCardProps) {\n  return (\n    <div className=\"bg-white dark:bg-[#2D1B1B] rounded-2xl p-6 border border-[#FFD4D4] dark:border-[#3D2626] hover:shadow-xl transition-all group\">\n      <div className={`w-12 h-12 ${color} rounded-xl flex items-center justify-center mb-4 group-hover:scale-110 transition-transform text-white`}>\n        {icon}\n      </div>\n      <div className=\"text-sm text-[#8B6B6B] dark:text-[#D4BABA] mb-1\">{label}</div>\n      <div className=\"text-3xl font-bold text-[#2D1B1B] dark:text-[#FFF5F5]\">{value}</div>\n    </div>\n  );\n}\n"
