      "name": "MiniCalendar",
      "type": "layout",
      "filePath": "/tmp/design-extract-1767109835357-dw7d2t/components/schedule/MiniCalendar.tsx",
      "template": "\"use client\";\n\nimport { motion } from \"framer-motion\";\n\ninterface Meeting {\n  scheduleType: string;\n  instructor: string;\n  dateRange: string;\n  days: string;\n  location: string;\n  time: string;\n  type: string;\n}\n\ninterface ScheduleSection {\n  course: string;\n  section: string;\n  instructor: string;\n  meetings: Meeting[];\n  credits: number;\n  fees: number;\n  status: string;\n  crn: string;\n  rmpRating: number | null;\n  rmpWTA: number | null;\n  rmpDifficulty: number | null;\n}\n\ninterface Props {\n  sections: ScheduleSection[];\n}\n\ninterface TimeBlock {\n  course: string;\n  color: string;\n  startMinutes: number;\n  durationMinutes: number;\n}\n\n// Parse time to minutes\nfunction parseTime(timeStr: string): { start: number; end: number } | null {\n  if (!timeStr || timeStr.toLowerCase() === \"online\") return null;\n  \n  const match = timeStr.match(/(\\d{1,2}):(\\d{2})\\s*(am|pm)\\s*-\\s*(\\d{1,2}):(\\d{2})\\s*(am|pm)/i);\n  if (!match) return null;\n\n  const parseTimeToMinutes = (hour: number, minute: number, ampm: string) => {\n    let h = hour;\n    if (ampm.toLowerCase() === 'pm' && h !== 12) h += 12;\n    if (ampm.toLowerCase() === 'am' && h === 12) h = 0;\n    return h * 60 + minute;\n  };\n\n  const start = parseTimeToMinutes(parseInt(match[1]), parseInt(match[2]), match[3]);\n  const end = parseTimeToMinutes(parseInt(match[4]), parseInt(match[5]), match[6]);\n\n  return { start, end };\n}\n\n// Get course color\nfunction getCourseColor(courseCode: string): string {\n  const colors = [\n    \"#E87C7C\", \"#4ECDC4\", \"#FFB84D\", \"#9B59B6\",\n    \"#E85D75\", \"#00B894\", \"#FDCB6E\", \"#6C5CE7\"\n  ];\n  const hash = courseCode.split('').reduce((a, b) => {\n    a = ((a << 5) - a) + b.charCodeAt(0);\n    return a & a;\n  }, 0);\n  return colors[Math.abs(hash) % colors.length];\n}\n\nexport default function MiniCalendar({ sections }: Props) {\n  const DAYS = [\"M\", \"T\", \"W\", \"R\", \"F\"];\n  const dayMap: { [key: string]: string } = {\n    'M': 'M', 'T': 'T', 'W': 'W', 'R': 'R', 'F': 'F'\n  };\n\n  // Process meetings\n  const scheduleByDay: { [day: string]: TimeBlock[] } = {};\n  DAYS.forEach(day => { scheduleByDay[day] = []; });\n\n  sections.forEach(section => {\n    section.meetings.forEach(meeting => {\n      if (!meeting.days || !meeting.time) return;\n      \n      const times = parseTime(meeting.time);\n      if (!times) return;\n\n      const color = getCourseColor(section.course);\n\n      meeting.days.split('').forEach(dayChar => {\n        const day = dayMap[dayChar];\n        if (day) {\n          scheduleByDay[day].push({\n            course: section.course,\n            color,\n            startMinutes: times.start,\n            durationMinutes: times.end - times.start\n          });\n        }\n      });\n    });\n  });\n\n  // FIXED: Always use 8 AM to 11 PM (consistent across all schedules)\n  const startHour = 8; // 8 AM\n  const endHour = 23; // 11 PM\n  \n  // Create time slots every 2 hours (8A, 10A, 12P, 2P, 4P, 6P, 8P, 10P)\n  const timeSlots: number[] = [];\n  for (let hour = startHour; hour <= endHour; hour += 2) {\n    timeSlots.push(hour * 60);\n  }\n\n  // Each time slot is 24px tall (compact!)\n  const slotHeight = 24;\n  const pixelsPerMinute = slotHeight / 60; // 0.4px per minute\n\n  return (\n    <div>\n      {/* Day headers */}\n      <div className=\"grid grid-cols-6 gap-0.5 mb-1\">\n        <div className=\"text-xs text-center text-[#8B6B6B] dark:text-[#D4BABA]\">Time</div>\n        {DAYS.map(day => (\n          <div key={day} className=\"text-xs font-semibold text-center text-[#2D1B1B] dark:text-[#FFF5F5]\">\n            {day}\n          </div>\n        ))}\n      </div>\n\n      {/* Time grid */}\n      {timeSlots.map((minutes) => {\n        const hour = minutes / 60;\n        const displayHour = hour > 12 ? hour - 12 : (hour === 0 ? 12 : hour);\n        const ampm = hour >= 12 ? 'P' : 'A';\n        \n        return (\n          <div key={minutes} className=\"grid grid-cols-6 gap-0.5 mb-0.5\">\n            {/* Time label */}\n            <div className=\"text-xs text-center text-[#8B6B6B] dark:text-[#D4BABA] flex items-center justify-center\" style={{ height: `${slotHeight}px` }}>\n              {displayHour}{ampm}\n            </div>\n            \n            {/* Day columns */}\n            {DAYS.map(day => (\n              <div \n                key={`${day}-${minutes}`} \n                className=\"relative bg-[#FFE5E5] dark:bg-[#3D2626] rounded-sm\" \n                style={{ height: `${slotHeight}px` }}\n              >\n                {/* Render blocks */}\n                {scheduleByDay[day]\n                  .filter(block => {\n                    const blockEndMinutes = block.startMinutes + block.durationMinutes;\n                    return block.startMinutes < minutes + 120 && blockEndMinutes > minutes;\n                  })\n                  .map((block, blockIndex) => {\n                    const visibleStart = Math.max(block.startMinutes, minutes);\n                    const visibleEnd = Math.min(block.startMinutes + block.durationMinutes, minutes + 120);\n                    const offsetMinutes = visibleStart - minutes;\n                    const visibleDuration = visibleEnd - visibleStart;\n                    \n                    const topOffset = offsetMinutes * pixelsPerMinute;\n                    const height = visibleDuration * pixelsPerMinute;\n\n                    return (\n                      <div\n                        key={blockIndex}\n                        className=\"absolute left-0.5 right-0.5 rounded-sm\"\n                        style={{\n                          backgroundColor: block.color,\n                          top: `${topOffset}px`,\n                          height: `${Math.max(height, 2)}px`\n                        }}\n                      ></div>\n                    );\n                  })}\n              </div>\n            ))}\n          </div>\n        );\n      })}\n    </div>\n  );\n}\n"
