      "template": "\"use client\";\n\nimport { motion } from \"framer-motion\";\n\ninterface Meeting {\n  scheduleType: string;\n  instructor: string;\n  dateRange: string;\n  days: string;\n  location: string;\n  time: string;\n  type: string;\n}\n\ninterface ScheduleSection {\n  course: string;\n  section: string;\n  instructor: string;\n  meetings: Meeting[];\n  credits: number;\n  fees: number;\n  status: string;\n  crn: string;\n  rmpRating: number | null;\n  rmpWTA: number | null;\n  rmpDifficulty: number | null;\n}\n\ninterface MiniScheduleVisualProps {\n  sections: ScheduleSection[];\n  className?: string;\n}\n\n// Parse time string to hour (e.g., \"10:00 am\" -> 10)\nfunction parseTimeToHour(timeStr: string): number {\n  if (!timeStr || timeStr.toLowerCase() === \"online\") return -1;\n  \n  const match = timeStr.match(/(\\d{1,2}):(\\d{2})\\s*(am|pm)/i);\n  if (!match) return -1;\n  \n  let hour = parseInt(match[1]);\n  const ampm = match[3].toLowerCase();\n  \n  if (ampm === \"pm\" && hour !== 12) hour += 12;\n  if (ampm === \"am\" && hour === 12) hour = 0;\n  \n  return hour;\n}\n\n// Get course color based on course code\nfunction getCourseColor(courseCode: string): string {\n  const colors = [\n    \"bg-[#E87C7C]\",\n    \"bg-[#4ECDC4]\",\n    \"bg-[#FFB84D]\",\n    \"bg-[#9B59B6]\",\n    \"bg-[#E85D75]\",\n    \"bg-[#00B894]\",\n    \"bg-[#FDCB6E]\",\n    \"bg-[#6C5CE7]\",\n  ];\n  \n  const hash = courseCode.split('').reduce((a, b) => {\n    a = ((a << 5) - a) + b.charCodeAt(0);\n    return a & a;\n  }, 0);\n  \n  return colors[Math.abs(hash) % colors.length];\n}\n\nexport default function MiniScheduleVisual({ sections, className = \"\" }: MiniScheduleVisualProps) {\n  const DAYS = [\"M\", \"T\", \"W\", \"R\", \"F\"];\n  \n  // Process sections to create mini schedule grid\n  const scheduleGrid: { [day: string]: { [hour: number]: Meeting & { course: string } } } = {};\n  \n  // Initialize grid\n  DAYS.forEach(day => {\n    scheduleGrid[day] = {};\n  });\n\n  // Fill grid with meetings\n  sections.forEach(section => {\n    section.meetings.forEach(meeting => {\n      if (meeting.days && meeting.time && meeting.time.toLowerCase() !== \"online\") {\n        const startHour = parseTimeToHour(meeting.time);\n        if (startHour !== -1) {\n          // Parse days string (e.g., \"MW\" -> [\"M\", \"W\"])\n          meeting.days.split('').forEach(dayChar => {\n            if (scheduleGrid[dayChar]) {\n              scheduleGrid[dayChar][startHour] = {\n                ...meeting,\n                course: section.course\n              };\n            }\n          });\n        }\n      }\n    });\n  });\n\n  // Get time range for compact display\n  const allHours = Object.values(scheduleGrid).flatMap(day => Object.keys(day).map(Number));\n  const minHour = allHours.length > 0 ? Math.min(...allHours) : 8;\n  const maxHour = allHours.length > 0 ? Math.max(...allHours) : 18;\n  const timeSlots = [];\n  \n  for (let hour = minHour; hour <= maxHour; hour++) {\n    timeSlots.push(hour);\n  }\n\n  return (\n    <div className={`${className}`}>\n      {/* Mini schedule grid */}\n      <div className=\"grid grid-cols-6 gap-1 mb-2\">\n        <div className=\"text-xs text-[#8B6B6B] dark:text-[#D4BABA] text-center\">\n          Time\n        </div>\n        {DAYS.map(day => (\n          <div key={day} className=\"text-xs font-semibold text-[#2D1B1B] dark:text-[#FFF5F5] text-center\">\n            {day}\n          </div>\n        ))}\n      </div>\n\n      <div className=\"space-y-1\">\n        {timeSlots.map((hour) => (\n          <div key={hour} className=\"grid grid-cols-6 gap-1\">\n            <div className=\"text-xs text-[#8B6B6B] dark:text-[#D4BABA] text-center py-1\">\n              {hour > 12 ? `${hour - 12}:00 PM` : hour === 12 ? '12:00 PM' : `${hour}:00 AM`}\n            </div>\n            {DAYS.map(day => {\n              const meeting = scheduleGrid[day][hour];\n              return (\n                <div key={`${day}-${hour}`} className=\"h-4 flex items-center justify-center\">\n                  {meeting ? (\n                    <div className={`w-full h-3 ${getCourseColor(meeting.course)} rounded-sm`}></div>\n                  ) : (\n                    <div className=\"w-full h-3 border border-dashed border-[#FFD4D4] dark:border-[#3D2626] rounded-sm opacity-30\"></div>\n                  )}\n                </div>\n              );\n            })}\n          </div>\n        ))}\n      </div>\n\n      {/* Course legend */}\n      <div className=\"mt-2 space-y-1\">\n        {sections.slice(0, 3).map((section, index) => (\n          <div key={index} className=\"flex items-center gap-1\">\n            <div className={`w-2 h-2 rounded ${getCourseColor(section.course)}`}></div>\n            <span className=\"text-xs text-[#8B6B6B] dark:text-[#D4BABA] truncate\">\n              {section.course.split(' ')[0]} {section.course.split(' ')[1]}\n            </span>\n          </div>\n        ))}\n        {sections.length > 3 && (\n          <div className=\"text-xs text-[#8B6B6B] dark:text-[#D4BABA]\">\n            +{sections.length - 3} more courses\n          </div>\n        )}\n      </div>\n    </div>\n  );\n}\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n"
