      "template": "\"use client\";\n\nimport { Calendar, dateFnsLocalizer } from 'react-big-calendar';\nimport { format, parse, startOfWeek, getDay } from 'date-fns';\nimport { enUS } from 'date-fns/locale';\nimport 'react-big-calendar/lib/css/react-big-calendar.css';\nimport './ProfessionalCalendar.css';\n\ninterface Meeting {\n  scheduleType: string;\n  instructor: string;\n  dateRange: string;\n  days: string;\n  location: string;\n  time: string;\n  type: string;\n}\n\ninterface Section {\n  course: string;\n  section: string;\n  instructor: string;\n  meetings: Meeting[];\n  credits: number;\n  fees: number;\n  status: string;\n  crn: string;\n  rmpRating: number | null;\n  rmpWTA: number | null;\n  rmpDifficulty: number | null;\n}\n\ninterface Props {\n  sections: Section[];\n  className?: string;\n}\n\nconst locales = {\n  'en-US': enUS,\n};\n\nconst localizer = dateFnsLocalizer({\n  format,\n  parse,\n  startOfWeek,\n  getDay,\n  locales,\n});\n\n// Get course color\nfunction getCourseColor(courseCode: string): string {\n  const colors = [\n    \"#E87C7C\", \"#4ECDC4\", \"#FFB84D\", \"#9B59B6\",\n    \"#E85D75\", \"#00B894\", \"#FDCB6E\", \"#6C5CE7\"\n  ];\n  const hash = courseCode.split('').reduce((a, b) => {\n    a = ((a << 5) - a) + b.charCodeAt(0);\n    return a & a;\n  }, 0);\n  return colors[Math.abs(hash) % colors.length];\n}\n\n// Parse time string to Date object\nfunction parseTimeToDate(timeStr: string, dayOfWeek: number): { start: Date; end: Date } | null {\n  if (!timeStr || timeStr.toLowerCase() === \"online\") return null;\n\n  const match = timeStr.match(/(\\d{1,2}):(\\d{2})\\s*(am|pm)\\s*-\\s*(\\d{1,2}):(\\d{2})\\s*(am|pm)/i);\n  if (!match) return null;\n\n  const parseTime = (hour: number, minute: number, ampm: string): { hours: number; minutes: number } => {\n    let h = hour;\n    if (ampm.toLowerCase() === 'pm' && h !== 12) h += 12;\n    if (ampm.toLowerCase() === 'am' && h === 12) h = 0;\n    return { hours: h, minutes: minute };\n  };\n\n  const startTime = parseTime(parseInt(match[1]), parseInt(match[2]), match[3]);\n  const endTime = parseTime(parseInt(match[4]), parseInt(match[5]), match[6]);\n\n  // Create dates for this week (using a fixed reference week)\n  const today = new Date();\n  const currentDay = today.getDay(); // 0 = Sunday, 1 = Monday, etc.\n  const diff = dayOfWeek - currentDay;\n  \n  const startDate = new Date(today);\n  startDate.setDate(today.getDate() + diff);\n  startDate.setHours(startTime.hours, startTime.minutes, 0, 0);\n\n  const endDate = new Date(today);\n  endDate.setDate(today.getDate() + diff);\n  endDate.setHours(endTime.hours, endTime.minutes, 0, 0);\n\n  return { start: startDate, end: endDate };\n}\n\nexport default function ProfessionalCalendar({ sections, className = \"\" }: Props) {\n  const dayMap: { [key: string]: number } = {\n    'M': 1, // Monday\n    'T': 2, // Tuesday\n    'W': 3, // Wednesday\n    'R': 4, // Thursday\n    'F': 5, // Friday\n  };\n\n  // Convert sections to calendar events\n  const events: any[] = [];\n\n  sections.forEach(section => {\n    const color = getCourseColor(section.course);\n    const courseDisplay = section.course.split(' ').slice(0, 2).join(' ');\n\n    section.meetings.forEach(meeting => {\n      if (!meeting.days || !meeting.time) return;\n\n      meeting.days.split('').forEach(dayChar => {\n        const dayOfWeek = dayMap[dayChar];\n        if (dayOfWeek !== undefined) {\n          const timeRange = parseTimeToDate(meeting.time, dayOfWeek);\n          if (timeRange) {\n            events.push({\n              title: courseDisplay,\n              start: timeRange.start,\n              end: timeRange.end,\n              resource: {\n                course: section.course,\n                instructor: section.instructor,\n                location: meeting.location,\n                type: meeting.type,\n                color: color,\n              },\n            });\n          }\n        }\n      });\n    });\n  });\n\n  // Custom event style\n  const eventStyleGetter = (event: any) => {\n    return {\n      style: {\n        backgroundColor: event.resource.color,\n        borderRadius: '6px',\n        opacity: 0.95,\n        color: 'white',\n        border: 'none',\n        display: 'block',\n        fontWeight: 600,\n        fontSize: '0.75rem',\n        padding: '4px 6px',\n      }\n    };\n  };\n\n  // Custom event component\n  const EventComponent = ({ event }: any) => (\n    <div className=\"flex flex-col leading-tight\">\n      <span className=\"font-bold text-xs\">{event.title}</span>\n      <span className=\"text-[10px] opacity-90\">{event.resource.location?.split(' ')[0]}</span>\n    </div>\n  );\n\n  return (\n    <div className={`professional-calendar-wrapper bg-white dark:bg-[#2D1B1B] rounded-xl border border-[#FFD4D4] dark:border-[#3D2626] overflow-hidden ${className}`}>\n      <div className=\"bg-gradient-to-r from-[#E87C7C]/10 to-[#FF6B6B]/10 px-6 py-4 border-b border-[#FFD4D4] dark:border-[#3D2626]\">\n        <h4 className=\"text-lg font-semibold text-[#2D1B1B] dark:text-[#FFF5F5]\">📅 Weekly Schedule</h4>\n      </div>\n      <div className=\"p-4\">\n        <Calendar\n          localizer={localizer}\n          events={events}\n          startAccessor=\"start\"\n          endAccessor=\"end\"\n          defaultView=\"work_week\"\n          views={['work_week']}\n          toolbar={false}\n          min={new Date(2025, 0, 1, 8, 0, 0)} // 8 AM\n          max={new Date(2025, 0, 1, 22, 0, 0)} // 10 PM\n          step={30}\n          timeslots={2}\n          eventPropGetter={eventStyleGetter}\n          components={{\n            event: EventComponent,\n          }}\n          style={{ height: 500 }}\n        />\n      </div>\n    </div>\n  );\n}\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n"
