      "template": "// components/ScheduleDetailsDialog/CourseDetails.tsx\n\"use client\";\n\nimport { Calendar, Clock, MapPin, User } from \"lucide-react\";\nimport type { Schedule } from \"./types\";\nimport FillHistoryInsights from \"../FillHistoryInsights\";\n\ninterface Props {\n  schedule: Schedule;\n}\n\nexport default function CourseDetails({ schedule }: Props) {\n  return (\n    <div className=\"space-y-4\">\n      <h3 className=\"text-xl font-bold text-[#2D1B1B] dark:text-[#FFF5F5] flex items-center gap-2\">\n        <Calendar className=\"w-5 h-5 text-[#E87C7C]\" />\n        Course Details\n      </h3>\n      {schedule.sections.map((section, index) => (\n        <div key={index} className=\"bg-white dark:bg-[#2D1B1B] rounded-xl p-4 border border-[#FFD4D4] dark:border-[#3D2626]\">\n          <div className=\"flex justify-between items-start mb-3\">\n            <div>\n              <h4 className=\"font-bold text-lg text-[#2D1B1B] dark:text-[#FFF5F5]\">{section.course}</h4>\n              <p className=\"text-sm text-[#8B6B6B] dark:text-[#D4BABA]\">Section {section.section} • CRN: {section.crn}</p>\n            </div>\n            <div className=\"text-right\">\n              <div className=\"text-sm font-bold text-[#E87C7C]\">{section.credits} credits</div>\n              <div className=\"text-xs text-[#8B6B6B] dark:text-[#D4BABA]\">${section.fees} fees</div>\n            </div>\n          </div>\n\n          <div className=\"flex items-center gap-2 mb-3\">\n            <User className=\"w-4 h-4 text-[#8B6B6B] dark:text-[#D4BABA]\" />\n            <span className=\"text-sm font-medium text-[#2D1B1B] dark:text-[#FFF5F5]\">{section.instructor}</span>\n            {section.rmpRating && (\n              <div className=\"ml-auto flex items-center gap-2 text-xs\">\n                <span className=\"text-[#8B6B6B] dark:text-[#D4BABA]\">RMP:</span>\n                <span className=\"font-bold text-[#E87C7C]\">{section.rmpRating}/5</span>\n                <span className=\"text-[#8B6B6B] dark:text-[#D4BABA]\">•</span>\n                <span className=\"font-bold text-[#E87C7C]\">{section.rmpWTA}% WTA</span>\n              </div>\n            )}\n          </div>\n\n          {section.meetings.map((meeting, mIdx) => (\n            <div key={mIdx} className=\"flex items-center gap-2 text-sm mb-2\">\n              <span className=\"px-2 py-1 bg-[#E87C7C] text-white rounded text-xs font-semibold\">\n                {meeting.type}\n              </span>\n              <Clock className=\"w-3 h-3 text-[#8B6B6B] dark:text-[#D4BABA]\" />\n              <span className=\"text-[#2D1B1B] dark:text-[#FFF5F5]\">{meeting.days} {meeting.time}</span>\n              {meeting.location !== 'Online' && (\n                <>\n                  <MapPin className=\"w-3 h-3 text-[#8B6B6B] dark:text-[#D4BABA]\" />\n                  <span className=\"text-[#8B6B6B] dark:text-[#D4BABA]\">{meeting.location}</span>\n                </>\n              )}\n            </div>\n          ))}\n\n          {/* Fill History Insights */}\n          {section.fill_history && section.fill_history.length > 0 && (\n            <div className=\"mt-4 pt-4 border-t border-[#FFD4D4] dark:border-[#3D2626]\">\n              <FillHistoryInsights\n                fill_history={section.fill_history}\n                capacity={section.capacity || 0}\n                currentActual={section.enrolled || undefined}\n                currentRemaining={section.seatsRemaining || undefined}\n                currentFillPercent={section.fillPercent || undefined}\n              />\n            </div>\n          )}\n        </div>\n      ))}\n    </div>\n  );\n}\n\n\n"
