      "template": "// components/ScheduleDetailsDialog/ScheduleCalendar.tsx\n\"use client\";\n\nimport { Clock } from \"lucide-react\";\nimport { getCourseColor, parseTime } from \"./utils\";\nimport type { Schedule } from \"./types\";\n\ninterface Props {\n  schedule: Schedule;\n}\n\nconst DAYS = ['M', 'T', 'W', 'R', 'F'];\nconst dayMap: { [key: string]: string } = {\n  'M': 'M', 'T': 'T', 'W': 'W', 'R': 'R', 'F': 'F'\n};\n\nexport default function ScheduleCalendar({ schedule }: Props) {\n  // Build schedule data\n  const scheduleByDay: { [day: string]: { startMinutes: number; durationMinutes: number; color: string; course: string }[] } = {};\n  DAYS.forEach(day => { scheduleByDay[day] = []; });\n\n  schedule.sections.forEach(section => {\n    section.meetings.forEach(meeting => {\n      if (!meeting.days || !meeting.time) return;\n      const times = parseTime(meeting.time);\n      if (!times) return;\n      const color = getCourseColor(section.course);\n      const courseDisplay = section.course;\n      meeting.days.split('').forEach(dayChar => {\n        const day = dayMap[dayChar];\n        if (day) {\n          scheduleByDay[day].push({\n            startMinutes: times.start,\n            durationMinutes: times.end - times.start,\n            color,\n            course: courseDisplay,\n          });\n        }\n      });\n    });\n  });\n\n  // Fixed 8 AM to 10 PM\n  const startHour = 8;\n  const endHour = 22;\n  const totalHours = endHour - startHour;\n  const startMinutes = startHour * 60;\n  const endMinutes = endHour * 60;\n  const totalMinutes = endMinutes - startMinutes;\n\n  // Larger calendar for dialog\n  const calendarHeight = 500;\n  const pixelsPerMinute = calendarHeight / totalMinutes;\n\n  // Create hour markers (every 2 hours)\n  const hourMarkers: number[] = [];\n  for (let hour = startHour; hour <= endHour; hour += 2) {\n    hourMarkers.push(hour);\n  }\n\n  return (\n    <div className=\"bg-gradient-to-br from-[#FFF5F5] to-[#FFE5E5] dark:from-[#1A0F0F] dark:to-[#2D1B1B] rounded-xl p-6 border border-[#FFD4D4] dark:border-[#3D2626]\">\n      <h3 className=\"text-xl font-bold text-[#2D1B1B] dark:text-[#FFF5F5] mb-4 flex items-center gap-2\">\n        <Clock className=\"w-5 h-5 text-[#E87C7C]\" />\n        Weekly Schedule\n      </h3>\n      \n      {/* Header */}\n      <div className=\"grid grid-cols-[60px_repeat(5,1fr)] gap-2 mb-3\">\n        <div className=\"text-xs font-semibold text-[#8B6B6B] dark:text-[#D4BABA] flex items-center justify-center\">\n          Time\n        </div>\n        {DAYS.map(day => (\n          <div key={day} className=\"text-sm font-bold text-center text-[#E87C7C]\">\n            {day}\n          </div>\n        ))}\n      </div>\n\n      {/* Calendar Grid */}\n      <div className=\"grid grid-cols-[60px_repeat(5,1fr)] gap-2\">\n        {/* Time column */}\n        <div className=\"relative\" style={{ height: `${calendarHeight}px` }}>\n          {hourMarkers.map((hour) => {\n            const position = ((hour - startHour) / totalHours) * 100;\n            const displayHour = hour > 12 ? hour - 12 : (hour === 0 ? 12 : hour);\n            const ampm = hour >= 12 ? 'PM' : 'AM';\n            return (\n              <div\n                key={hour}\n                className=\"absolute text-xs text-[#8B6B6B] dark:text-[#D4BABA] font-medium\"\n                style={{\n                  top: `${position}%`,\n                  transform: 'translateY(-50%)',\n                  right: '4px'\n                }}\n              >\n                {displayHour} {ampm}\n              </div>\n            );\n          })}\n        </div>\n\n        {/* Day columns */}\n        {DAYS.map(day => (\n          <div\n            key={day}\n            className=\"relative bg-white dark:bg-[#2D1B1B] rounded-lg border-2 border-[#FFD4D4] dark:border-[#3D2626] overflow-hidden\"\n            style={{ height: `${calendarHeight}px` }}\n          >\n            {/* Hour lines */}\n            {hourMarkers.map(hour => {\n              const position = ((hour - startHour) / totalHours) * 100;\n              return (\n                <div\n                  key={hour}\n                  className=\"absolute w-full border-t border-dashed border-[#FFD4D4] dark:border-[#3D2626] opacity-40\"\n                  style={{ top: `${position}%` }}\n                />\n              );\n            })}\n\n            {/* Class blocks */}\n            {scheduleByDay[day].map((block, index) => {\n              const topPosition = (block.startMinutes - startMinutes) * pixelsPerMinute;\n              const height = block.durationMinutes * pixelsPerMinute;\n\n              return (\n                <div\n                  key={index}\n                  className=\"absolute left-1 right-1 rounded-md shadow-md flex flex-col items-center justify-center text-sm font-bold text-white overflow-hidden hover:scale-105 transition-transform\"\n                  style={{\n                    backgroundColor: block.color,\n                    top: `${topPosition}px`,\n                    height: `${Math.max(height, 20)}px`,\n                    opacity: 0.95,\n                  }}\n                >\n                  {height > 30 && (\n                    <span className=\"truncate px-1\">{block.course}</span>\n                  )}\n                </div>\n              );\n            })}\n          </div>\n        ))}\n      </div>\n\n      {/* Legend */}\n      <div className=\"mt-4 pt-4 border-t border-[#FFD4D4] dark:border-[#3D2626]\">\n        <div className=\"flex flex-wrap gap-3\">\n          {[...new Set(schedule.sections.map(s => s.course))].map((course, i) => (\n            <div key={i} className=\"flex items-center gap-2\">\n              <div\n                className=\"w-3 h-3 rounded-full\"\n                style={{ backgroundColor: getCourseColor(course) }}\n              />\n              <span className=\"text-sm font-medium text-[#2D1B1B] dark:text-[#FFF5F5]\">\n                {course}\n              </span>\n            </div>\n          ))}\n        </div>\n      </div>\n    </div>\n  );\n}\n\n\n"
